mapper.sql:
FROM t_order a



		AND a.order_id = #{orderId}


		AND a.product_id = #{productId}


		AND a.order_no = #{orderNo}


		AND a.create_date between #{startCreateDate} and #{endCreateDate}


		AND a.create_date > #{startCreateDate}


		AND a.create_date < #{endCreateDate}


		AND a.order_money = #{orderMoney}


		AND a.update_date = #{updateDate}


		AND a.orderIntegration = #{orderintegration}


		AND a.create_by = #{createBy}


		AND a.integration_amount = #{integrationAmount}


		AND a.state = #{state}


		AND a.freight = #{freight}


		AND a.order_count_money = #{orderCountMoney}


		AND a.deal_state = #{dealState}


		AND a.buyer_remark = #{buyerRemark}


		AND a.dealpay_type = #{dealpayType}


		AND a.receiver_address = #{receiverAddress}


		AND a.receiver_mobile = #{receiverMobile}


		AND a.receiver_name = #{receiverName}


		AND a.receiver_phone = #{receiverPhone}


		AND a.receiver_postcode = #{receiverPostcode}


		AND a.buyer_name = #{buyerName}


		AND a.buy_userid = #{buyUserid}


		AND a.create_time = #{createTime}


		AND a.pay_time = #{payTime}


		AND a.recvfee_time = #{recvfeeTime}


		AND a.recv_username = #{recvUsername}


		AND a.freight_company = #{freightCompany}


		AND a.freight_number = #{freightNumber}


		AND a.express_code = #{expressCode}


		AND a.channel_balance = #{channelBalance}


		AND a.return_balance = #{returnBalance}


		AND a.update_by = #{updateBy}


		AND a.remarks = #{remarks}

mapper.select:
SELECT 

FROM t_order a

WHERE a.order_id = #{orderId}

mapper.update:
UPDATE t_order
order_id = #{orderId}, product_id = #{productId}, order_no = #{orderNo}, order_money = #{orderMoney}, update_date = #{updateDate}, orderIntegration = #{orderintegration}, integration_amount = #{integrationAmount}, state = #{state}, freight = #{freight}, order_count_money = #{orderCountMoney}, deal_state = #{dealState}, buyer_remark = #{buyerRemark}, dealpay_type = #{dealpayType}, receiver_address = #{receiverAddress}, receiver_mobile = #{receiverMobile}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, receiver_postcode = #{receiverPostcode}, buyer_name = #{buyerName}, buy_userid = #{buyUserid}, create_time = #{createTime}, pay_time = #{payTime}, recvfee_time = #{recvfeeTime}, recv_username = #{recvUsername}, freight_company = #{freightCompany}, freight_number = #{freightNumber}, express_code = #{expressCode}, channel_balance = #{channelBalance}, return_balance = #{returnBalance}, update_by = #{updateBy}, remarks = #{remarks},
WHERE order_id = #{orderId}
